import React, { useState, useEffect } from 'react';
import axios from "axios";
import { Star, StarHalf, StarOff } from 'lucide-react';
import { ChevronDown } from "lucide-react";


const StarRating = ({ rating }) => {
  const fullStars = Math.floor(rating);
  const hasHalfStar = rating % 1 >= 0.5;
  const emptyStars = 5 - fullStars - (hasHalfStar ? 1 : 0);

  return (
    <div className="flex items-center space-x-1 text-yellow-400">
      {[...Array(fullStars)].map((_, i) => (
        <Star key={`full-${i}`} className="w-4 sm:w-5 h-4 sm:h-5 fill-yellow-400" />
      ))}
      {hasHalfStar && <StarHalf className="w-4 sm:w-5 h-4 sm:h-5 fill-yellow-400" />}
      {[...Array(emptyStars)].map((_, i) => (
        <StarOff key={`empty-${i}`} className="w-4 sm:w-5 h-4 sm:h-5" />
      ))}
      <span className="ml-2 text-xs sm:text-sm text-gray-700">({rating})</span>
    </div>
  );
};

const CreditCardOffer = () => {
  const [cards, setCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedBanks, setSelectedBanks] = useState([]);
  const [expandedFeatures, setExpandedFeatures] = useState(false);

  useEffect(() => {
    const fetchCards = async () => {
      try {
        const res = await axios.get('http://localhost:3003/api/getcard');
        setCards(res.data.data || res.data);
      } catch (err) {
        console.error('Error fetching cards:', err);
        setError('Failed to load cards');
      } finally {
        setLoading(false);
      }
    };
    fetchCards();
  }, []);

  const toggleBankSelection = (bankName) => {
    setSelectedBanks(prev => 
      prev.includes(bankName) 
        ? prev.filter(bank => bank !== bankName) 
        : [...prev, bankName]
    );
  };

  const filteredCards = selectedBanks.length > 0
    ? cards.filter(card => selectedBanks.includes(card.bankName))
    : cards;

  return (
    <div className="min-h-full bg-gray-50 py-4 sm:py-6 md:py-8 px-3 sm:px-4 md:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="grid grid-cols-1 lg:grid-cols-12 gap-4 sm:gap-6 md:gap-8">
        
          <div className="lg:col-span-3 bg-white rounded-lg sm:rounded-xl shadow-md p-4 sm:p-6 h-fit lg:sticky lg:top-4 sm:top-6">
            <h2 className="text-base sm:text-lg font-semibold border-b pb-2 text-red-600 mb-3 sm:mb-4">Filters</h2>
            <div className="mb-4 sm:mb-6">
              <h3 className="text-sm sm:text-md font-semibold mb-2 sm:mb-3">Provider's Name</h3>
              <div className="space-y-1 sm:space-y-2">
  {[
    { name: "Yes Bank", img: "data:image/png;base64," },
    { name: "ICICI Bank", img: "data:image/jpeg;base64," },
    { name: "Bank Of Baroda", img: "data:image/png;base64," },
    { name: "IDFC Bank", img: "data:image/png;base64," },
    { name: "HDFC Bank", img: "data:image/jpeg;base64," },
    { name: "Axis Bank", img: "/banks/axis.png" },
    { name: "HSBC Bank", img: "/banks/hsbc.png" },
    { name: "Kotak Mahindra Bank", img: "/banks/kotak.png" },
  ]
    .slice(0, expandedFeatures ? undefined : 5)
    .map(({ name, img }, index) => (
      <div key={index} className="flex items-center">
        <input
          type="checkbox"
          id={`bank-${index}`}
          checked={selectedBanks.includes(name)}
          onChange={() => toggleBankSelection(name)}
          className="h-3.5 sm:h-4 w-3.5 sm:w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
        />
        <label htmlFor={`bank-${index}`} className="ml-2 sm:ml-3 text-xs sm:text-sm text-gray-700 flex items-center gap-2">
          <img src={img} alt={name} className="w-7 h-12 sm:w-5 sm:h-5 object-contain text-bold" />
          {name}
        </label>
      </div>
    ))}
  <button
    onClick={() => setExpandedFeatures(!expandedFeatures)}
    className="text-blue-600 text-xs sm:text-sm hover:underline"
  >
    {expandedFeatures ? 'See Less' : 'See More'}
  </button>
</div>

            </div>
            <div className="w-full max-w-sm mx-auto shadow-sm bg-white">
      <div className="border-t">
        <div className="w-full flex justify-between items-center px-4 py-3 text-left text-[#2c317a] font-medium hover:bg-gray-100 cursor-pointer">
          Provider’s Name
          <ChevronDown className="h-5 w-5" />
        </div>
      </div>
      <div className="border-t">
        <div className="w-full flex justify-between items-center px-4 py-3 text-left text-[#2c317a] font-medium hover:bg-gray-100 cursor-pointer">
          APR%
          <ChevronDown className="h-5 w-5" />
        </div>
      </div>
      <div className="border-t">
        <div className="w-full flex justify-between items-center px-4 py-3 text-left text-[#2c317a] font-medium hover:bg-gray-100 cursor-pointer">
          Annual Fee
          <ChevronDown className="h-5 w-5" />
        </div>
      </div>
      <div className="border-t">
        <div className="w-full flex justify-between items-center px-4 py-3 text-left text-[#2c317a] font-medium hover:bg-gray-100 cursor-pointer">
          Credit Score
          <ChevronDown className="h-5 w-5" />
        </div>
      </div>
      <div className="border-t">
        <div className="w-full flex justify-between items-center px-4 py-3 text-left text-[#2c317a] font-medium hover:bg-gray-100 cursor-pointer">
          Rating
          <ChevronDown className="h-5 w-5" />
        </div>
      </div>
      <div className="border-t border-b">
        <div className="w-full flex justify-between items-center px-4 py-3 text-left text-[#2c317a] font-medium hover:bg-gray-100 cursor-pointer">
          Card Benefits
          <ChevronDown className="h-5 w-5" />
        </div>
      </div>
    </div>
          
                     </div>

        
          <div className="lg:col-span-9">
            {loading ? (
              <div className="flex justify-center items-center h-48 sm:h-64">
                <div className="animate-spin rounded-full h-8 sm:h-12 w-8 sm:w-12 border-t-2 border-b-2 border-blue-500"></div>
              </div>
            ) : error ? (
              <div className="bg-red-100 border border-red-400 text-red-700 px-3 sm:px-4 py-2 sm:py-3 rounded">
                {error}
              </div>
            ) : filteredCards.length === 0 ? (
              <div className="bg-white rounded-lg sm:rounded-xl shadow-md p-4 sm:p-6 md:p-8 text-center">
                <p className="text-sm sm:text-base text-gray-600">No cards match your selected filters</p>
                <button 
                  onClick={() => setSelectedBanks([])} 
                  className="mt-2 sm:mt-3 text-xs sm:text-sm text-blue-600 hover:underline"
                >
                  Clear all filters
                </button>
              </div>
            ) : (
              <div className="space-y-3 sm:space-y-4 md:space-y-6">
                {filteredCards.map((card, index) => (
                  <div key={card.id || index} className="bg-white rounded-lg sm:rounded-xl shadow-md overflow-hidden">
                    <div className="p-3 sm:p-4 md:p-6">
                      <div className="flex flex-col sm:flex-row gap-3 sm:gap-4 md:gap-6">
                   
                        <div className="flex-shrink-0 mx-auto sm:mx-0">
                          <img
                            src={card.cardImage || "https://www.cardexpert.in/wp-content/uploads/2020/10/axis-bank-ace-credit-card-1.jpg"}
                            alt={card.cardName}
                            className="w-40 sm:w-48 md:w-56 h-24 sm:h-32 md:h-40 object-contain rounded-lg"
                          />
                        </div>
                        
                 
                        <div className="flex-grow">
                          <h2 className="text-base sm:text-lg md:text-xl font-bold text-indigo-900">{card.cardName}</h2>
                          <p className="text-xs sm:text-sm text-indigo-900 mb-2 sm:mb-3 md:mb-4">{card.cardSlug}</p>
                          <p className="text-xs sm:text-sm text-indigo-900 mb-2 sm:mb-3 md:mb-4">
                            {card.seoDescription}
                          </p>
                          
                          <div className="flex flex-wrap gap-2 sm:gap-3">
                            <button className="bg-indigo-900 text-white px-3 sm:px-4 py-1 sm:py-2 rounded-md text-xs sm:text-sm hover:bg-blue-700 transition">
                              Apply Now
                            </button>
                            <button className="border border-indigo-900 text-indigo-900 px-3 sm:px-4 py-1 sm:py-2 rounded-md text-xs sm:text-sm hover:bg-blue-50 transition">
                              Check Eligibility
                            </button>
                          </div>
                        </div>
                    
                        <div className="flex-shrink-0 flex flex-col items-end mt-2 sm:mt-0">
                          <div className="mb-2 sm:mb-3 md:mb-4">
                            <p className="font-bold text-gray-900 text-xs sm:text-sm">MoneyBIP Rating</p>
                            <StarRating rating={card.moneybipRating || 4.5} />
                          </div>
                          <label className="flex items-center gap-1 sm:gap-2 text-xs sm:text-sm text-gray-900">
                            <input type="checkbox" className="accent-blue-600 h-3 sm:h-4 w-3 sm:w-4" />
                            Add to Compare
                          </label>
                        </div>
                      </div>
                    </div>
                
                    <div className="border-t px-3 sm:px-4 md:px-6 py-2 sm:py-3 md:py-4">
                      <div className="grid grid-cols-1 md:grid-cols-3 gap-3 sm:gap-4 md:gap-6">
                        <div className="md:col-span-2">
                          <h3 className="text-sm sm:text-base md:text-lg font-bold text-gray-800 mb-1 sm:mb-2 md:mb-3">Features</h3>
                          <ul className="list-disc text-xs sm:text-sm text-black font-medium pl-3 sm:pl-4 md:pl-5 space-y-1">
                            <li>Vistara Privileges: Enjoy complimentary Club Vistara Silver membership, airport lounge access</li>
                            <li>Accelerated Rewards: Earn Club Vistara Points on every spend</li>
                            <li>Welcome Benefits: Complimentary Club Vistara Base Points</li>
                            <li>Travel and Lifestyle Benefits: Discounts on dining, hotel bookings</li>
                            <li>Milestone Rewards: Earn bonus Club Vistara Points</li>
                          </ul>
                        </div>
                        
                                         <div className="border-l pl-3 sm:pl-4 md:pl-6">
                          <div className="mb-2 sm:mb-3 md:mb-4">
                            <p className="text-xs sm:text-sm text-gray-600 font-bold">Annual Fees</p>
                            <p className="text-base sm:text-lg md:text-xl font-bold text-gray-900">₹{card.annualFee || "1,500"}</p>
                          </div>
                          <div className="mb-2 sm:mb-3 md:mb-4">
                            <p className="text-xs sm:text-sm text-gray-600 font-bold">Joining Fees</p>
                            <p className="text-base sm:text-lg md:text-xl font-bold text-gray-900">{card.joiningFee || "No Cost"}</p>
                          </div>
                          <div>
                            <p className="text-xs sm:text-sm text-gray-600 font-bold">Recommended Credit Score</p>
                            <p className="text-base sm:text-lg md:text-xl font-bold text-gray-900">{card.creditScoreMin || "550"}-{card.creditScoreMax || "600"}</p>
                            <a href="#" className="text-blue-600 text-xs sm:text-sm hover:underline">Check your credit score here</a>
                          </div>
                        </div>
                      </div>
                    </div>
                                      <div className="border-t px-3 sm:px-4 md:px-6 py-2 sm:py-3 md:py-4 bg-white">
                      <h3 className="text-sm sm:text-base md:text-lg font-bold text-gray-800 mb-1 sm:mb-2 md:mb-3">Welcome Offers</h3>
                      <p className="text-xs sm:text-sm text-gray-700 mb-1 sm:mb-2 md:mb-3">
                        While specific welcome offers may change over time, typical welcome benefits include:
                      </p>
                      <ul className="list-disc text-xs sm:text-sm text-black font-medium pl-3 sm:pl-4 md:pl-5 space-y-1">
                        <li>Bonus Club Vistara Points upon card activation</li>
                        <li>Joining Fee Waiver</li>
                        <li>Complimentary Lounge Access</li>
                        <li>Additional Rewards on Initial Spends</li>
                      </ul>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CreditCardOffer;